var size = 1;
var procsize = 100;
var topl = 0;
$(".size_btn .min").click(function(){
    if(size > 0.1){
        size -= 0.1;
        procsize -= 10;
    }
    $("#rez_items").attr("style","transform: scale("+size+") ");
    $(".size").html(procsize+"%");

    return false;
});
$(".size_btn .pls").click(function(){
    if(size < 3){
        size += 0.1;
        procsize += 10;
    }
    $(".size").html(procsize+"%");
    $("#rez_items").attr("style","transform: scale("+size+")");
    return false;
});


$(".btn_file").click(function(){
    $("#input_lang").val($(".lang-choise").val());
    if ($("#radio1").prop('checked'))
        $("#has_public_confirm").val("True");
    else
        $("#has_public_confirm").val("False");

    if ($("#checkbox1").prop('checked'))
        $("#find_orientation").val("True");
    else
        $("#find_orientation").val("False");

    if ($("#checkbox2").prop('checked'))
        $("#process_2_sides").val("True");
    else
        $("#process_2_sides").val("False");

});

