 $('#mail_dopp').keydown(function(e) {
    if(e.keyCode === 13) {
         var test_item = false;
        var help_oitem = $(this).val()+"<span>x</span>";
        $(".mail_list>div").each(function (index, el){
            if($(el).html() == help_oitem)
                test_item = true;
        });

        if(test_item == false){
            var item_list = $(".mail_list").html();
            item_list += "<div class='item'>"+$(this).val()+"<span>x</span></div>";
            $(".mail_list").html(item_list);
        }


        $(".mail_list>div span").click(function(){
            $(this).parent(".item").addClass("active");
            $(".mail_list>.item.active").detach();
        });
        return false;
    }
  });


